mapper.select:
	    SELECT record_no, adm_id, tagid, exit_date, exit_code
	    FROM   freeway_changecard
        
           
               WHERE  #{param2} >= exit_date AND exit_date >= #{param1}
           
           
               WHERE ((tagid < ${maxLoop} AND adm_id != '1') OR tagid is null) 
            
         
         AND rownum < ${maxRecords}
